import React, { createContext, useState } from "react";

export const PostContext = createContext();

const createRandomPost = () => {
  const randomTitle = `Title ${Math.floor(Math.random() * 100)}`;
  const randomBody = `Body ${Math.floor(Math.random() * 1000)}`;
  return { title: randomTitle, body: randomBody };
};

export const PostProvider = ({ children }) => {
  const [posts, setPosts] = useState([]);
  const [searchQuery, setSearchQuery] = useState("");

  const handleAddPost = (newPost) => {
    setPosts([...posts, newPost]);
  };

  const handleClearPosts = () => {
    setPosts([]);
  };

  const searchedPosts = posts.filter(
    (post) =>
      post.title.includes(searchQuery) || post.body.includes(searchQuery)
  );

  return (
    <PostContext.Provider
      value={{
        posts: searchedPosts,
        onAddPost: handleAddPost,
        onClearPosts: handleClearPosts,
        searchQuery,
        setSearchQuery,
      }}
    >
      {children}
    </PostContext.Provider>
  );
};
